/* Bread Crumb - http://thecodeplayer.com/walkthrough/css3-breadcrumb-navigation */
@media(min-width: 800px)
{
  .mybreadcrumb {
    /*centering*/
    display: block;
    border: 1px solid #ccc;
    border-radius: 5px;
    background: #f5f5f5;
    height: 40px;
  }

  .mybreadcrumb a {
    text-decoration: none;
    outline: none;
    display: block;
    float: right;
    font-size: 12px;
    line-height: 36px;
    color: white;
    /*need more margin on the right of links to accomodate the numbers*/
    padding: 0 30px 0 10px;
    background: #666;
    background: linear-gradient(#666, #333);
    position: relative;
  }
  /*since the first link does not have a triangle before it we can reduce the right padding to make it look consistent with other links*/
  .mybreadcrumb a:first-child {
    padding-right: 16px;
    border-radius:  0 5px 5px 0; /*to match with the parent's radius*/
  }
  .mybreadcrumb a:first-child:before {
    font-size: 32px;
  }
  .mybreadcrumb a:last-child {
    border-radius: 5px 0 0 5px; /*this was to prevent glitches on hover*/
    padding-left: 20px;
  }

  /*hover/active styles*/
  .mybreadcrumb a.active, .mybreadcrumb a:hover{
    background: #333;
    background: linear-gradient(#333, #000);
  }
  .mybreadcrumb a.active:after, .mybreadcrumb a:hover:after {
    background: #333;
    background: linear-gradient(135deg, #333, #000);
  }

  /*adding the arrows for the breadcrumbs using rotated pseudo elements*/
  .mybreadcrumb a:after {
    content: '';
    position: absolute;
    top: 0; 
    left: -18px; /*half of square's length*/
    /*same dimension as the line-height of .breadcrumb a */
    width: 36px; 
    height: 36px;
    /*as you see the rotated square takes a larger height. which makes it tough to position it properly. So we are going to scale it down so that the diagonals become equal to the line-height of the link. We scale it to 70.7% because if square's: 
    length = 1; diagonal = (1^2 + 1^2)^0.5 = 1.414 (pythagoras theorem)
    if diagonal required = 1; length = 1/1.414 = 0.707*/
    transform: scale(0.707) rotate(45deg);
    /*we need to prevent the arrows from getting buried under the next link*/
    z-index: 1;
    /*background same as links but the gradient will be rotated to compensate with the transform applied*/
    background: #666;
    background: linear-gradient(135deg, #666, #333);
    /*stylish arrow design using box shadow*/
    box-shadow: 
      -2px 2px 0 2px rgba(0, 0, 0, 0.4), 
      -3px 3px 0 2px rgba(255, 255, 255, 0.1);
    /*
    5px - for rounded arrows and 
    50px - to prevent hover glitches on the border created using shadows*/
    border-radius: 0 50px 5px 0;
  }
  /*we dont need an arrow after the last link*/
  .mybreadcrumb a:last-child:after {
    content: none;
  }

  .mybreadcrumb a, .mybreadcrumb a:after {
    background: white;
    color: black;
    transition: all 0.5s;
  }
  .mybreadcrumb a:hover, .mybreadcrumb a.active, 
  .mybreadcrumb a:hover:after, .mybreadcrumb a.active:after{
    background: #f5f5f5;
  }
}

